const state = {

};

function getFunctionBody (fn) {
  var c = fn.toString();
  if (c.startsWith('function ')) {
    return c.slice(c.indexOf('{') + 1, c.lastIndexOf('}')).trim();
  }
  c = c.split('=>')[1].trim();
  if (c[0] === '{' && c[c.length - 1] === '}') {
    return c.slice(1, -1).trim();
  }
  return `return ${c};`;
}

module.exports = getFunctionBody;
