'use strict';

const _ = {
  extend: require('./extend'),
  trim: require('./trim'),
  capitalize: require('./capitalize'),
  dasherize: require('./dasherize'),
};

const state = {

};

function heading(str, cap = true) {
  return _
    .dasherize(str)
    .split('-')
    .map(w => cap ? _.capitalize(w) : w)
    .join(' ');
}

module.exports = heading;
