'use strict';

const $ = {
  type: require('./type'),
  extend: require('./extend'),
  getTypes: require('./get-types'),
  capitalize: require('./capitalize'),
};

function is (val, type) {
  return $.type(val) === type;
}

$.getTypes()
  .filter(type => !['class'].includes(type))
  .forEach(type => {
    var name = `${type}`;
    if (name === 'isNan') name = 'isNaN';
    is[name] = val => $.type(val) === type;
  });

$.extend(is, {
  Class: require('./is-class'),
  empty: require('./is-empty'),
  equal: require('./is-equal'),
  standardTag: require('./is-standard-tag'),
});

module.exports = is;
