var datetime = {
  full
};

function full(d) {
  return (`
    ${z(d.getDate())}.${(z(d.getMonth() + 1))}.${z(d.getFullYear())}
    ${z(d.getHours())}:${z(d.getMinutes())}:${z(d.getSeconds())}
  `).trim().replace(/\s+/mgi, ' ');
}

function z(val) {
  val = val + '';
  return val.length === 1 ? `0${val}` : val;
}

export default datetime;
