class Util {

  flatten (item) {
    var self = this;
    if (Array.isArray(item)) {
      return _.flatten(item);
    }
    var data = {};
    for (var i in item) {
      if (!item.hasOwnProperty(i)) continue;
      if ((typeof item[i]) == 'object') {
        var flat = self.flatten(item[i]);
        for (var x in flat) {
          if (!flat.hasOwnProperty(x)) continue;
          data[i + '.' + x] = flat[x];
        }
      } else {
        data[i] = item[i];
      }
    }
    return data;
  }

}

module.exports = new Util();
