function flatten(obj, parentName) {
  var data = {};
  parentName = parentName || '';
  Object.keys(obj).forEach((name) => {
    var val = obj[name];
    name = parentName ? parentName + '.' + name : name;
    if (typeof val !== 'object') {
      data[name] = val;
      return;
    }
    Object.assign(data, flatten(val, name));
  });
  return data;
}

export default flatten;
