import apply from './apply';

class CortElement extends HTMLElement {
  enable (name) {
    var self = this;
    self.dataset[name] = name;
    self.classList.add(`flag--${name}`);
    return self;
  }

  disable (name) {
    var self = this;
    delete self.dataset[name];
    self.classList.remove(`flag--${name}`);
    return self;
  }

  toggle (name) {
    var self = this;
    return self.dataset[name] ? self.disable(name) : self.enable(name);
  }
}

apply(CortElement);

export default CortElement;
