'use strict';

const _ = {
  create: require('../create'),
};

const state = {

};

function loadScript (url) {
  return new Promise((resolve, reject) => {
    _.create({
      tag: 'script',
      $src: url,
      appendTo: 'body',
      $_load (ev) {
        resolve(ev);
      },
      $_error (err) {
        reject(err);
      },
    });
  });
}

module.exports = loadScript;
