'use strict';

const _ = {
  sample: require('./sample'),
  extend: require('./extend'),
};

const items = require('./lorem-text.js');

function lorem() {
  return _.sample(items);
}

function sen(count = 3) {
  var result = '';
  for (var i = 0; i < count; i++) {
    result = `${result} ${lorem()}`;
  }
  return result.trim();
}

function paragraphs (count = 3, sent = 3) {
  var result = '';
  for (var i = 0; i < count; i++) {
    result = `${result}\n\n${sen(sent)}`;
  }
  return result.trim();
}

function headline (count = 3) {
  var s = '';
  while (s.includes(',') || s.includes('.') || s.length < 10) {
    s = lorem().split(' ').slice(0, count).join(' ');
  }
  return s;
}

_.extend(lorem, {
  sen,
  s: sen,
  paragraphs,
  p: paragraphs,
  headline,
  h: headline,
})

module.exports = lorem;
