'use strict';

const _ = {

};

const state = {

};

function once (elem = document, name = 'click') {
  return new Promise((resolve, reject) => {
    elem.addEventListener(name, handler, false);
    function handler(ev) {
      elem.removeEventListener(name, handler);
      resolve(ev);
    }
  });
}

module.exports = once;
