'use strict';

const _ = {
  each: require('./each'),
};

const state = {

};

function protoAliases (Class, data) {
  var proto = Class.prototype;
  _.each(data, (a, name) => {
    var val = proto[name],
      aliases = Array.isArray(a) ? a : [a];
    _.each(aliases, alias => {
      proto[alias] = val;
    });
  });
}

module.exports = protoAliases;
