'use strict';

const _ = {
  clone: require('./clone'),
  inspectObject: require('./inspect-object'),
  isPrototype: require('./is-prototype'),
};

function setPrototypes (...items) {
  for (let i = 0; i < items.length; i++) {
    let item = items[i],
      next = items[i + 1];
    if (!next) break;
    Object.setPrototypeOf(item, Object.create(next));
  }
  return items[0];
}

module.exports = setPrototypes;

function createClass(obj, Parent = null) {
  var Class = function Class (...args) {
    var self = obj;
    if (Parent) Parent.call(self, ...args);
    return obj;
  };
  Object.setPrototypeOf(obj, Class.prototype);
  Parent ? Object.setPrototypeOf(Class.prototype, Object.create(Parent.prototype)) : '';
  return Class;
}
