'use strict';

function min(v, minValue = -Infinity) {
  var val = +v;
  return val < minValue ? minValue : val;
}

function max(v, maxValue = Infinity) {
  var val = +v;
  return val > maxValue ? maxValue : val;
}

function bounds(v, minValue = -Infinity, maxValue = Infinity) {
  var val = +v;
  return max(min(val, minValue), maxValue);
}

Object.assign(range, {
  min, max, bounds,
});

function range (start, finish, step = 1) {
  if (arguments.length === 1) {
    finish = start;
    start = 0;
  }
  var counter = start,
    nums = [];
  while (counter <= finish) {
    nums.push(counter);
    counter += step;
  }
  return nums;
}

module.exports = range;
