'use strict';

function sortBy (items, prop) {
  var fn = _.type(prop) === 'function' ? prop : function (a, b) {
      if (!prop) return a > b ? 1 : -1;
      var key = prop[0] === '-' ? prop.slice(1) : prop;
      return a[key] > b[key] ? 1 : -1;
    },
    result = items.slice(0).sort(fn);
  return prop && prop[0] === '-'
    ? result.reverse()
    : result;
}

module.exports = sortBy;
