'use strict';

const Wrapper = require('./wrapper');

const _ = {

};

const state = {

};

function wrap (...args) {
  var name = args.length === 1
      ? `dom-wrap-${Date.now()}`
      : args[0],
    cb = args[args.length - 1],
    wrapper = new Wrapper(name, cb);
  state.wrappers[name] = wrapper;
  return wrapper;
}

module.exports = wrap;
